import React from 'react';
import {StyleSheet, Text, View, TouchableOpacity,Image} from 'react-native';
export default function Servicios(){
    return(
        <View style={styles.container}>
            <View>
                <TouchableOpacity  style={styles.containert} onPress={() => navigation.navigate("Servicios")}>
                <LinearGradient
                // Button Linear Gradient
                colors={['#4c669f', '#3b5998']}
                style={styles.buttont}>
                <Text style={styles.textt}>REFRIGERACION</Text>
                </LinearGradient>
                </TouchableOpacity>
            </View>
            <View>
            <TouchableOpacity  style={styles.containert} onPress={() => navigation.navigate("Servicios")}>
                <LinearGradient
                // Button Linear Gradient
                colors={['#4c669f', '#3b5998']}
                style={styles.buttont}>
                <Image source={require('./assets/3g.png')}
                style={{width: 150, height:150}}
                />
                <Text style={styles.textt}>REFRIGERACION</Text>
                </LinearGradient>
                </TouchableOpacity>
            </View>
        </View>
    )

}
const styles = StyleSheet.create({
    container: {
      backgroundColor: '#f1f1f1',
      flex: 1,  
      alignItems: 'center',
      justifyContent: 'center',
    },
    dropdown: {
      padding: 10,
      paddingStart: 20,
      width: '85%',
      height: 60,
      backgroundColor: 'white',
      marginTop: 20,
      borderBottomColor: 'gray',
      borderBottomWidth: 0.5,
      borderRadius: 12,
    },
    icon: {
      marginRight: 5,
    },
    placeholderStyle: {
      fontSize: 16,
    },
    selectedTextStyle: {
      fontSize: 16,
    },
    iconStyle: {
      width: 20,
      height: 20,
    },
    inputSearchStyle: {
      height: 40,
      fontSize: 16,
    },
    containert: {
      flex:0,
      alignItems: 'center',
      width: 200,
      marginTop: 40
  },
  textt: {
      fontSize: 14,
      color: '#fff',
      fontWeight: 'bold'
  },
  buttont: {
      width: '80%',
      height: 50,
      borderRadius:25,
      padding:10,
      alignItems: 'center',
      justifyContent: 'center'
  }
